"use client";

import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "react";
import FastMarquee from "react-fast-marquee";
import type { MarqueeProps as ScrollProps } from "react-fast-marquee";

export type MarqueeProps = HTMLAttributes<HTMLDivElement>;

export const InfiniteScroll = ({ className, ...props }: ScrollProps) => (
    <div
        className={cn("relative w-full overflow-hidden", className)}
        {...props}
    />
);

export type InfiniteScrollContentProps = ScrollProps;

export const InfiniteScrollContent = ({
    loop = 0,
    autoFill = false,
    pauseOnHover = false,
    ...props
}: InfiniteScrollContentProps) => (
    <FastMarquee
        loop={loop}
        autoFill={autoFill}
        pauseOnHover={pauseOnHover}
        {...props}
    />
);

export type InfiniteScrollFadeProps = HTMLAttributes<HTMLDivElement> & {
    side: "left" | "right";
};

export const InfiniteScrollFade = ({
    className,
    side,
    ...props
}: InfiniteScrollFadeProps) => (
    <div
        className={cn(
            "absolute bottom-0 top-0 z-10 h-full w-24 from-background to-transparent",
            side === "left"
                ? "left-0 bg-gradient-to-r"
                : "right-0 bg-gradient-to-l",
            className
        )}
        {...props}
    />
);

export type InfiniteScrollItemProps = HTMLAttributes<HTMLDivElement>;

export const InfiniteScrollItem = ({
    className,
    ...props
}: InfiniteScrollItemProps) => (
    <div
        className={cn("mx-2 flex-shrink-0 object-contain", className)}
        {...props}
    />
);
